mapper.select:
SELECT data.kvskey
FROM data, fields
WHERE fields.field = #{field} AND fields.value = #{value} AND data.kvskey = fields.kvskey AND data.kvstable = #{kvstable}

SELECT data.kvskey
FROM data, fields
WHERE fields.field = #{field} AND fields.value  <=  #{value} AND data.kvskey = fields.kvskey AND data.kvstable = #{kvstable}

SELECT data.kvskey
FROM data, fields
